sequence.script:

var createdAfter=mc.getProperty("uri.var.createdAfter");
var createdBefore=mc.getProperty("uri.var.createdBefore");
var idGreaterThan = mc.getProperty("uri.var.idGreaterThan");
var listEmailId = mc.getProperty("uri.var.listEmailId");
var dripProgramActionId=mc.getProperty("uri.var.dripProgramActionId");
var emailTemplateId=mc.getProperty("uri.var.emailTemplateId");
var trackerRedirectId = mc.getProperty("uri.var.trackerRedirectId");
var limit = mc.getProperty("uri.var.limit");
var format=mc.getProperty("uri.var.format");

var urlQuery = '';
if (createdAfter != null && createdAfter != ""){
   urlQuery += 'created_after=' + createdAfter + '&';
}
if (createdBefore != null && createdBefore != ""){
   urlQuery += 'created_before=' + createdBefore + '&';
}
if (idGreaterThan != null && idGreaterThan != ""){
   urlQuery += 'id_greater_than=' + idGreaterThan + '&';
}
if (listEmailId != null && listEmailId != ""){
   urlQuery += 'list_email_id=' + listEmailId + '&';
}
if (dripProgramActionId != null && dripProgramActionId != ""){
   urlQuery += 'drip_program_action_id=' + dripProgramActionId + '&';
}
if (emailTemplateId != null && emailTemplateId != ""){
   urlQuery += 'email_template_id=' + emailTemplateId + '&';
}
if (trackerRedirectId != null && trackerRedirectId != ""){
   urlQuery += 'tracker_redirect_id=' + trackerRedirectId + '&';
}
if (limit != null && limit != ""){
   urlQuery += 'limit=' + limit + '&';
}
if (format != null && format != ""){
   urlQuery += 'format=' + format + '&';;
}
if (urlQuery != null && urlQuery != ""){
   urlQuery = '&' + urlQuery.toString().substring(0, urlQuery.toString().lastIndexOf("&"));
}
mc.setProperty('uri.var.urlQuery',urlQuery);
