}
if (sortOrder != null && sortOrder != ""){
   urlQuery += 'sort_order=' + sortOrder + '&';
}
if (output != null && output != ""){
   urlQuery += 'output=' + output + '&';
}
if (format != null && format != ""){
   urlQuery += 'format=' + format + '&';;
